    if (_trustedOrgs.length > 0) {
      _addTrustedOrganizations(_trustedOrgs);
    }
    _setThreshold(__num, __denom);
  }

  /**
   * @inheritdoc IQuorum
   */
  function getThreshold() external view virtual returns (uint256 num_, uint256 denom_) {
    return (_num, _denom);
  }

  /**
   * @inheritdoc IQuorum
