    _governorList[_index] = _governorList[_count - 1];
    _governorList.pop();

    delete _bridgeVoterWeight[_bridgeVoterList[_index]];
    _bridgeVoterList[_index] = _bridgeVoterList[_count - 1];
    _bridgeVoterList.pop();
  }

