pragma solidity ^0.8.9;

import "../../libraries/AddressArrayUtils.sol";
import "../../interfaces/staking/ICandidateStaking.sol";
import "./BaseStaking.sol";

abstract contract CandidateStaking is BaseStaking, ICandidateStaking {
  /// @dev The minimum threshold for being a validator candidate.
  uint256 internal _minValidatorStakingAmount;

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   */
  uint256[50] private ______gap;

  /**
   * @inheritdoc ICandidateStaking
   */
  function minValidatorStakingAmount() public view override returns (uint256) {
    return _minValidatorStakingAmount;
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function setMinValidatorStakingAmount(uint256 _threshold) external override onlyAdmin {
    _setMinValidatorStakingAmount(_threshold);
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function applyValidatorCandidate(
    address _candidateAdmin,
    address _consensusAddr,
    address payable _treasuryAddr,
    address _bridgeOperatorAddr,
    uint256 _commissionRate
  ) external payable override nonReentrant {
    require(!isActivePoolAdmin(msg.sender), "CandidateStaking: pool admin is active");

    uint256 _amount = msg.value;
    address payable _poolAdmin = payable(msg.sender);
    _applyValidatorCandidate(
      _poolAdmin,
      _candidateAdmin,
      _consensusAddr,
      _treasuryAddr,
      _bridgeOperatorAddr,
      _commissionRate,
      _amount
    );

    PoolDetail storage _pool = _stakingPool[_consensusAddr];
    _pool.admin = _poolAdmin;
    _pool.addr = _consensusAddr;
    _activePoolAdminMapping[_poolAdmin] = _consensusAddr;

    _stake(_stakingPool[_consensusAddr], _poolAdmin, _amount);
    emit PoolApproved(_consensusAddr, _poolAdmin);
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function requestUpdateCommissionRate(
    address _consensusAddr,
    uint256 _effectiveDaysOnwards,
    uint256 _commissionRate
  ) external override poolExists(_consensusAddr) onlyPoolAdmin(_stakingPool[_consensusAddr], msg.sender) {
    _validatorContract.execRequestUpdateCommissionRate(_consensusAddr, _effectiveDaysOnwards, _commissionRate);
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function deprecatePools(address[] calldata _pools) external override onlyValidatorContract {
    if (_pools.length == 0) {
      return;
    }

    uint256 _amount;
    for (uint _i = 0; _i < _pools.length; _i++) {
      PoolDetail storage _pool = _stakingPool[_pools[_i]];
      // Deactivate the pool admin in the active mapping.
      delete _activePoolAdminMapping[_pool.admin];

      // Deduct and transfer the self staking amount to the pool admin.
      _amount = _pool.stakingAmount;
      if (_amount > 0) {
        _deductStakingAmount(_pool, _amount);
        if (!_unsafeSendRON(payable(_pool.admin), _amount)) {
          emit StakingAmountTransferFailed(_pool.addr, _pool.admin, _amount, address(this).balance);
        }
      }
    }

    emit PoolsDeprecated(_pools);
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function stake(address _consensusAddr) external payable override noEmptyValue poolExists(_consensusAddr) {
    _stake(_stakingPool[_consensusAddr], msg.sender, msg.value);
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function unstake(address _consensusAddr, uint256 _amount) external override nonReentrant poolExists(_consensusAddr) {
    require(_amount > 0, "CandidateStaking: invalid amount");
    address _delegator = msg.sender;
    PoolDetail storage _pool = _stakingPool[_consensusAddr];
    uint256 _remainAmount = _pool.stakingAmount - _amount;
    require(_remainAmount >= _minValidatorStakingAmount, "CandidateStaking: invalid staking amount left");

    _unstake(_pool, _delegator, _amount);
    require(_sendRON(payable(_delegator), _amount), "CandidateStaking: could not transfer RON");
  }

  /**
   * @inheritdoc ICandidateStaking
   */
  function requestRenounce(address _consensusAddr)
    external
    override
    poolExists(_consensusAddr)
    onlyPoolAdmin(_stakingPool[_consensusAddr], msg.sender)
  {
    _validatorContract.requestRevokeCandidate(_consensusAddr, _waitingSecsToRevoke);
  }

  /**
   * @dev See `ICandidateStaking-applyValidatorCandidate`
   */
  function _applyValidatorCandidate(
    address payable _poolAdmin,
    address _candidateAdmin,
    address _consensusAddr,
    address payable _treasuryAddr,
    address _bridgeOperatorAddr,
    uint256 _commissionRate,
    uint256 _amount
  ) internal {
    require(_sendRON(_poolAdmin, 0), "CandidateStaking: pool admin cannot receive RON");
    require(_sendRON(_treasuryAddr, 0), "CandidateStaking: treasury cannot receive RON");
    require(_amount >= _minValidatorStakingAmount, "CandidateStaking: insufficient amount");

    require(
      _poolAdmin == _candidateAdmin && _candidateAdmin == _treasuryAddr,
      "CandidateStaking: three interaction addresses must be of the same"
    );

    address[] memory _diffAddrs = new address[](3);
    _diffAddrs[0] = _poolAdmin;
    _diffAddrs[1] = _consensusAddr;
    _diffAddrs[2] = _bridgeOperatorAddr;
    require(
      !AddressArrayUtils.hasDuplicate(_diffAddrs),
      "CandidateStaking: three operation addresses must be distinct"
    );

    _validatorContract.grantValidatorCandidate(
      _candidateAdmin,
      _consensusAddr,
      _treasuryAddr,
      _bridgeOperatorAddr,
      _commissionRate
    );
  }

  /**
   * @dev See `ICandidateStaking-stake`
   */
  function _stake(
    PoolDetail storage _pool,
    address _requester,
    uint256 _amount
  ) internal onlyPoolAdmin(_pool, _requester) {
    _pool.stakingAmount += _amount;
    _changeDelegatingAmount(_pool, _requester, _pool.stakingAmount, _pool.stakingTotal + _amount);
    _pool.lastDelegatingTimestamp[_requester] = block.timestamp;
    emit Staked(_pool.addr, _amount);
  }

  /**
   * @dev See `ICandidateStaking-unstake`
   */
  function _unstake(
    PoolDetail storage _pool,
    address _requester,
    uint256 _amount
  ) internal onlyPoolAdmin(_pool, _requester) {
    require(_amount <= _pool.stakingAmount, "CandidateStaking: insufficient staking amount");
    require(
      _pool.lastDelegatingTimestamp[_requester] + _cooldownSecsToUndelegate <= block.timestamp,
      "CandidateStaking: unstake too early"
    );

    _pool.stakingAmount -= _amount;
    _changeDelegatingAmount(_pool, _requester, _pool.stakingAmount, _pool.stakingTotal - _amount);
    emit Unstaked(_pool.addr, _amount);
  }

  /**
   * @dev Deducts from staking amount of the validator `_consensusAddr` for `_amount`.
   *
   * Emits the event `Unstaked`.
   *
   * @return The actual deducted amount
   */
  function _deductStakingAmount(PoolDetail storage _pool, uint256 _amount) internal virtual returns (uint256);

  /**
   * @dev Sets the minimum threshold for being a validator candidate.
   *
   * Emits the `MinValidatorStakingAmountUpdated` event.
   *
   */
  function _setMinValidatorStakingAmount(uint256 _threshold) internal {
    _minValidatorStakingAmount = _threshold;
    emit MinValidatorStakingAmountUpdated(_threshold);
  }
}
","keccak256":"0xe80c374e9425772e3a50721371b108148b325bef59536e9ce7ca963f81d49f7e","license":"MIT"},"contracts/ronin/staking/DelegatorStaking.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "../../interfaces/staking/IDelegatorStaking.sol";
import "./BaseStaking.sol";

abstract contract DelegatorStaking is BaseStaking, IDelegatorStaking {
  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   */
  uint256[50] private ______gap;

  /**
   * @inheritdoc IDelegatorStaking
   */
  function delegate(address _consensusAddr) external payable noEmptyValue poolExists(_consensusAddr) {
    require(!isActivePoolAdmin(msg.sender), "DelegatorStaking: admin of an active pool cannot delegate");
    _delegate(_stakingPool[_consensusAddr], msg.sender, msg.value);
  }

  /**
   * @inheritdoc IDelegatorStaking
   */
  function undelegate(address _consensusAddr, uint256 _amount) external nonReentrant {
    address payable _delegator = payable(msg.sender);
    _undelegate(_stakingPool[_consensusAddr], _delegator, _amount);
    require(_sendRON(_delegator, _amount), "DelegatorStaking: could not transfer RON");
  }

  /**
   * @inheritdoc IDelegatorStaking
   */
  function bulkUndelegate(address[] calldata _consensusAddrs, uint256[] calldata _amounts) external nonReentrant {
    require(
      _consensusAddrs.length > 0 && _consensusAddrs.length == _amounts.length,
      "DelegatorStaking: invalid array length"
    );

    address payable _delegator = payable(msg.sender);
    uint256 _total;

    for (uint _i = 0; _i < _consensusAddrs.length; _i++) {
      _total += _amounts[_i];
      _undelegate(_stakingPool[_consensusAddrs[_i]], _delegator, _amounts[_i]);
    }

    require(_sendRON(_delegator, _total), "DelegatorStaking: could not transfer RON");
  }

  /**
   * @inheritdoc IDelegatorStaking
   */
  function redelegate(
    address _consensusAddrSrc,
    address _consensusAddrDst,
    uint256 _amount
  ) external nonReentrant poolExists(_consensusAddrDst) {
    address _delegator = msg.sender;
    _undelegate(_stakingPool[_consensusAddrSrc], _delegator, _amount);
    _delegate(_stakingPool[_consensusAddrDst], _delegator, _amount);
  }

  /**
   * @inheritdoc IDelegatorStaking
   */
  function claimRewards(address[] calldata _consensusAddrList)
    external
    override
    nonReentrant
    returns (uint256 _amount)
  {
    _amount = _claimRewards(msg.sender, _consensusAddrList);
    _transferRON(payable(msg.sender), _amount);
  }

  /**
   * @inheritdoc IDelegatorStaking
   */
  function delegateRewards(address[] calldata _consensusAddrList, address _consensusAddrDst)
    external
    override
    nonReentrant
    poolExists(_consensusAddrDst)
    returns (uint256 _amount)
  {
    return _delegateRewards(msg.sender, _consensusAddrList, _consensusAddrDst);
  }

  /**
   * @inheritdoc IDelegatorStaking
   */
  function getRewards(address _user, address[] calldata _poolAddrList)
    external
    view
    returns (uint256[] memory _rewards)
  {
    address _consensusAddr;
    uint256 _period = _validatorContract.currentPeriod();
    _rewards = new uint256[](_poolAddrList.length);

    for (uint256 _i = 0; _i < _poolAddrList.length; _i++) {
      _consensusAddr = _poolAddrList[_i];
      _rewards[_i] = _getReward(_consensusAddr, _user, _period, getStakingAmount(_consensusAddr, _user));
    }
  }

  /**
   * @dev Delegates from a validator address.
   *
   * Requirements:
   * - The delegator is not the pool admin.
   *
   * Emits the `Delegated` event.
   *
   * Note: This function does not verify the `msg.value` with the amount.
   *
   */
  function _delegate(
    PoolDetail storage _pool,
    address _delegator,
    uint256 _amount
  ) internal notPoolAdmin(_pool, _delegator) {
    _changeDelegatingAmount(
      _pool,
      _delegator,
      _pool.delegatingAmount[_delegator] + _amount,
      _pool.stakingTotal + _amount
    );
    _pool.lastDelegatingTimestamp[_delegator] = block.timestamp;
    emit Delegated(_delegator, _pool.addr, _amount);
  }

  /**
   * @dev Undelegates from a validator address.
   *
   * Requirements:
   * - The delegator is not the pool admin.
   * - The amount is larger than 0.
   * - The delegating amount is larger than or equal to the undelegating amount.
   *
   * Emits the `Undelegated` event.
   *
   * Note: Consider transferring back the amount of RON after calling this function.
   *
   */
  function _undelegate(
    PoolDetail storage _pool,
    address _delegator,
    uint256 _amount
  ) private notPoolAdmin(_pool, _delegator) {
    require(_amount > 0, "DelegatorStaking: invalid amount");
    require(_pool.delegatingAmount[_delegator] >= _amount, "DelegatorStaking: insufficient amount to undelegate");
    require(
      _pool.lastDelegatingTimestamp[_delegator] + _cooldownSecsToUndelegate < block.timestamp,
      "DelegatorStaking: undelegate too early"
    );
    _changeDelegatingAmount(
      _pool,
      _delegator,
      _pool.delegatingAmount[_delegator] - _amount,
      _pool.stakingTotal - _amount
    );
    emit Undelegated(_delegator, _pool.addr, _amount);
  }

  /**
   * @dev Claims rewards from the pools `_poolAddrList`.
   * Note: This function does not transfer reward to user.
   */
  function _claimRewards(address _user, address[] calldata _poolAddrList) internal returns (uint256 _amount) {
    for (uint256 _i = 0; _i < _poolAddrList.length; _i++) {
      _amount += _claimReward(_poolAddrList[_i], _user);
    }
  }

  /**
   * @dev Claims the rewards and delegates them to the consensus address.
   */
  function _delegateRewards(
    address _user,
    address[] calldata _poolAddrList,
    address _poolAddrDst
  ) internal returns (uint256 _amount) {
    _amount = _claimRewards(_user, _poolAddrList);
    _delegate(_stakingPool[_poolAddrDst], _user, _amount);
  }
}
","keccak256":"0xf398428c3d6123e20f0f05c6d0ccaaea6f3e6366a52a66620c82b77954ce66ad","license":"MIT"},"contracts/ronin/staking/RewardCalculation.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "../../interfaces/staking/IRewardPool.sol";
import "../../libraries/Math.sol";

/**
 * @title RewardCalculation contract
 * @dev This contract mainly contains the methods to calculate reward for staking contract.
 */
abstract contract RewardCalculation is IRewardPool {
  /// @dev Mapping from pool address => period number => accumulated rewards per share (one unit staking)
  mapping(address => mapping(uint256 => PeriodWrapper)) private _accumulatedRps;
  /// @dev Mapping from the pool address => user address => the reward info of the user
  mapping(address => mapping(address => UserRewardFields)) private _userReward;
  /// @dev Mapping from the pool address => reward pool fields
  mapping(address => PoolFields) private _stakingPool;

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   */
  uint256[50] private ______gap;

  /**
   * @inheritdoc IRewardPool
   */
  function getReward(address _poolAddr, address _user) external view returns (uint256) {
    return _getReward(_poolAddr, _user, _currentPeriod(), getStakingAmount(_poolAddr, _user));
  }

  /**
   * @inheritdoc IRewardPool
   */
  function getStakingAmount(address _poolAddr, address _user) public view virtual returns (uint256);

  /**
   * @inheritdoc IRewardPool
   */
  function getStakingTotal(address _poolAddr) public view virtual returns (uint256);

  /**
   * @dev Returns the reward amount that user claimable.
   */
  function _getReward(
    address _poolAddr,
    address _user,
    uint256 _latestPeriod,
    uint256 _latestStakingAmount
  ) internal view returns (uint256) {
    UserRewardFields storage _reward = _userReward[_poolAddr][_user];

    if (_reward.lastPeriod == _latestPeriod) {
      return _reward.debited;
    }

    uint256 _aRps;
    uint256 _lastPeriodReward;
    PoolFields storage _pool = _stakingPool[_poolAddr];
    PeriodWrapper storage _wrappedArps = _accumulatedRps[_poolAddr][_reward.lastPeriod];

    if (_wrappedArps.lastPeriod > 0) {
      // Calculates the last period reward if the aRps at the period is set
      _aRps = _accumulatedRps[_poolAddr][_reward.lastPeriod].inner;
      _lastPeriodReward = _reward.minAmount * (_aRps - _reward.aRps);
    } else {
      // Fallbacks to the previous aRps in case the aRps is not set
      _aRps = _reward.aRps;
    }

    uint256 _newPeriodsReward = _latestStakingAmount * (_pool.aRps - _aRps);
    return _reward.debited + (_lastPeriodReward + _newPeriodsReward) / 1e18;
  }

  /**
   * @dev Syncs the user reward.
   *
   * Emits the event `UserRewardUpdated` once the debit amount is updated.
   * Emits the event `PoolSharesUpdated` once the pool share is updated.
   *
   * Note: The method should be called whenever the user's staking amount changes.
   *
   */
  function _syncUserReward(
    address _poolAddr,
    address _user,
    uint256 _newStakingAmount
  ) internal {
    uint256 _period = _currentPeriod();
    PoolFields storage _pool = _stakingPool[_poolAddr];
    uint256 _lastShares = _pool.shares.inner;

    // Updates the pool shares if it is outdated
    if (_pool.shares.lastPeriod < _period) {
      _pool.shares = PeriodWrapper(getStakingTotal(_poolAddr), _period);
    }

    UserRewardFields storage _reward = _userReward[_poolAddr][_user];
    uint256 _currentStakingAmount = getStakingAmount(_poolAddr, _user);
    uint256 _debited = _getReward(_poolAddr, _user, _period, _currentStakingAmount);

    if (_reward.debited != _debited) {
      _reward.debited = _debited;
      emit UserRewardUpdated(_poolAddr, _user, _debited);
    }

    _syncMinStakingAmount(_pool, _reward, _period, _newStakingAmount, _currentStakingAmount);
    _reward.aRps = _pool.aRps;
    _reward.lastPeriod = _period;

    if (_pool.shares.inner != _lastShares) {
      emit PoolSharesUpdated(_period, _poolAddr, _pool.shares.inner);
    }
  }

  /**
   * @dev Syncs the minimum staking amount of an user in the current period.
   */
  function _syncMinStakingAmount(
    PoolFields storage _pool,
    UserRewardFields storage _reward,
    uint256 _latestPeriod,
    uint256 _newStakingAmount,
    uint256 _currentStakingAmount
  ) internal {
    if (_reward.lastPeriod < _latestPeriod) {
      _reward.minAmount = _currentStakingAmount;
    }

    uint256 _minAmount = Math.min(_reward.minAmount, _newStakingAmount);
    uint256 _diffAmount = _reward.minAmount - _minAmount;
    if (_diffAmount > 0) {
      _reward.minAmount = _minAmount;
      require(_pool.shares.inner >= _diffAmount, "RewardCalculation: invalid pool shares");
      _pool.shares.inner -= _diffAmount;
    }
  }

  /**
   * @dev Claims the settled reward for a specific user.
   *
   * Emits the `PendingRewardUpdated` event and the `SettledRewardUpdated` event.
   *
   * Note: This method should be called before transferring rewards for the user.
   *
   */
  function _claimReward(address _poolAddr, address _user) internal returns (uint256 _amount) {
    uint256 _latestPeriod = _currentPeriod();
    _amount = _getReward(_poolAddr, _user, _latestPeriod, getStakingAmount(_poolAddr, _user));
    emit RewardClaimed(_poolAddr, _user, _amount);

    UserRewardFields storage _reward = _userReward[_poolAddr][_user];
    _reward.debited = 0;
    _reward.lastPeriod = _latestPeriod;
    _reward.aRps = _stakingPool[_poolAddr].aRps;
    emit UserRewardUpdated(_poolAddr, _user, 0);
  }

  /**
   * @dev Records the amount of rewards `_rewards` for the pools `_poolAddrs`.
   *
   * Emits the event `PoolsUpdated` once the contract recorded the rewards successfully.
   * Emits the event `PoolsUpdateFailed` once the input array lengths are not equal.
   * Emits the event `PoolUpdateConflicted` when the pool is already updated in the period.
   *
   * Note: This method should be called once at the period ending.
   *
   */
  function _recordRewards(
    address[] memory _poolAddrs,
    uint256[] calldata _rewards,
    uint256 _period
  ) internal {
    if (_poolAddrs.length != _rewards.length) {
      emit PoolsUpdateFailed(_period, _poolAddrs, _rewards);
      return;
    }

    uint256 _rps;
    uint256 _count;
    address _poolAddr;
    uint256 _stakingTotal;
    uint256[] memory _aRps = new uint256[](_poolAddrs.length);
    uint256[] memory _shares = new uint256[](_poolAddrs.length);
    address[] memory _conflicted = new address[](_poolAddrs.length);

    for (uint _i = 0; _i < _poolAddrs.length; _i++) {
      _poolAddr = _poolAddrs[_i];
      PoolFields storage _pool = _stakingPool[_poolAddr];
      _stakingTotal = getStakingTotal(_poolAddr);

      if (_accumulatedRps[_poolAddr][_period].lastPeriod == _period) {
        _conflicted[_count++] = _poolAddr;
        continue;
      }

      // Updates the pool shares if it is outdated
      if (_pool.shares.lastPeriod < _period) {
        _pool.shares = PeriodWrapper(_stakingTotal, _period);
      }

      // The rps is 0 if no one stakes for the pool
      _rps = _pool.shares.inner == 0 ? 0 : (_rewards[_i] * 1e18) / _pool.shares.inner;
      _aRps[_i - _count] = _pool.aRps += _rps;
      _accumulatedRps[_poolAddr][_period] = PeriodWrapper(_pool.aRps, _period);
      if (_pool.shares.inner != _stakingTotal) {
        _pool.shares.inner = _stakingTotal;
      }
      _shares[_i - _count] = _pool.shares.inner;
      _poolAddrs[_i - _count] = _poolAddr;
    }

    if (_count > 0) {
      assembly {
        mstore(_conflicted, _count)
        mstore(_poolAddrs, sub(mload(_poolAddrs), _count))
      }
      emit PoolsUpdateConflicted(_period, _conflicted);
    }

    if (_poolAddrs.length > 0) {
      emit PoolsUpdated(_period, _poolAddrs, _aRps, _shares);
    }
  }

  /**
   * @dev Returns the current period.
   */
  function _currentPeriod() internal view virtual returns (uint256);
}
